module("Page titles");test("Page titles must be present and non-empty",function(assert){var title=document.querySelector("title");if(title&&title.parentNode)title.parentNode.removeChild(title);var config={scope:document.documentElement,ruleName:"pageWithoutTitle",expected:axs.constants.AuditResult.FAIL};assert.runRule(config);var head=document.querySelector("head"),blankTitle=document.createElement("title");head.appendChild(blankTitle);assert.runRule(config);blankTitle.textContent="foo";config.expected=axs.constants.AuditResult.PASS;assert.runRule(config);blankTitle.parentNode.replaceChild(title,blankTitle)});