module("RequiredOwnedAriaRoleMissing");test("Explicit role on container and required elements all explicitly present",function(assert){var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("div"));container.setAttribute("role","list");for(var i=0,item;4>i;i++){item=container.appendChild(document.createElement("span"));item.setAttribute("role","listitem")}var config={ruleName:"requiredOwnedAriaRoleMissing",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config)});test("Explicit role on container and required elements all explicitly present via aria-owns",function(assert){var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("div")),siblingContainer=fixture.appendChild(document.createElement("div")),ids=[];container.setAttribute("role","list");for(var i=0;4>i;i++){var id=ids[i]="item"+i,item=siblingContainer.appendChild(document.createElement("span"));item.setAttribute("role","listitem");item.setAttribute("id",id)}container.setAttribute("aria-owns",ids.join(" "));equal(container.childNodes.length,0);var config={ruleName:"requiredOwnedAriaRoleMissing",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config)});test("Explicit role on container and required elements missing",function(assert){var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("div"));container.setAttribute("role","list");var config={ruleName:"requiredOwnedAriaRoleMissing",expected:axs.constants.AuditResult.FAIL,elements:[container]};assert.runRule(config)});test("Explicit role on aria-busy container and required elements missing",function(assert){var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("div"));container.setAttribute("role","list");container.setAttribute("aria-busy","true");var config={ruleName:"requiredOwnedAriaRoleMissing",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config)});test("Explicit role on container and required elements all implicitly present",function(assert){var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("ul"));container.setAttribute("role","list");for(var i=0;4>i;i++){container.appendChild(document.createElement("li"))}var config={ruleName:"requiredOwnedAriaRoleMissing",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config)});test("No role",function(assert){var fixture=document.getElementById("qunit-fixture");fixture.appendChild(document.createElement("div"));var config={ruleName:"requiredOwnedAriaRoleMissing",expected:axs.constants.AuditResult.NA};assert.runRule(config)});test("Role with no required elements",function(assert){var fixture=document.getElementById("qunit-fixture"),container=fixture.appendChild(document.createElement("div"));container.setAttribute("role","checkbox");var config={ruleName:"requiredOwnedAriaRoleMissing",expected:axs.constants.AuditResult.NA};assert.runRule(config)});