module("tabIndex values");test("tabIndex is 0 or -1 passes the audit",function(assert){var fixture=document.getElementById("qunit-fixture"),listItem=document.createElement("li"),heading=document.createElement("h2");fixture.appendChild(listItem);fixture.appendChild(heading);listItem.tabIndex=-1;heading.tabIndex=0;var config={ruleName:"tabIndexGreaterThanZero",expected:axs.constants.AuditResult.PASS,elements:[]};assert.runRule(config)});test("tabIndex with a positive integer fails the audit",function(assert){var fixture=document.getElementById("qunit-fixture"),listItem=document.createElement("li");fixture.appendChild(listItem);listItem.tabIndex=1;var config={ruleName:"tabIndexGreaterThanZero",expected:axs.constants.AuditResult.FAIL,elements:[listItem]};assert.runRule(config)});