QUnit.extend(QUnit.assert,{runRule:function(config,message){var ruleName=config.ruleName,auditConfig=new axs.AuditConfiguration({auditRulesToRun:[ruleName],scope:config.scope||document.getElementById("qunit-fixture"),walkDom:!1});if(config.ignoreSelectors)auditConfig.ignoreSelectors(ruleName,config.ignoreSelectors);var actual=axs.Audit.run(auditConfig);this.equal(actual.length,1,"Only one rule should have run");var result=actual[0];this.equal(result.rule.name,ruleName,"The correct rule should have run");if(message)this.equal(result.result,config.expected,message);else this.equal(result.result,config.expected);if(config.elements)this.deepEqual(result.elements,config.elements,"The correct number of elements should be included")}});