var parse=require("../"),test=require("tape");test("numeric short args",function(t){t.plan(2);t.deepEqual(parse(["-n123"]),{n:123,_:[]});t.deepEqual(parse(["-123","456"]),{1:!0,2:!0,3:456,_:[]})});test("short",function(t){t.deepEqual(parse(["-b"]),{b:!0,_:[]},"short boolean");t.deepEqual(parse(["foo","bar","baz"]),{_:["foo","bar","baz"]},"bare");t.deepEqual(parse(["-cats"]),{c:!0,a:!0,t:!0,s:!0,_:[]},"group");t.deepEqual(parse(["-cats","meow"]),{c:!0,a:!0,t:!0,s:"meow",_:[]},"short group next");t.deepEqual(parse(["-h","localhost"]),{h:"localhost",_:[]},"short capture");t.deepEqual(parse(["-h","localhost","-p","555"]),{h:"localhost",p:555,_:[]},"short captures");t.end()});test("mixed short bool and capture",function(t){t.same(parse(["-h","localhost","-fp","555","script.js"]),{f:!0,p:555,h:"localhost",_:["script.js"]});t.end()});test("short and long",function(t){t.deepEqual(parse(["-h","localhost","-fp","555","script.js"]),{f:!0,p:555,h:"localhost",_:["script.js"]});t.end()});