'use strict';function pad(number){var n=number.toString();return 1===n.length?"0"+n:n}function toISOString(date){return date.getUTCFullYear()+"-"+pad(date.getUTCMonth()+1)+"-"+pad(date.getUTCDate())+"T"+pad(date.getUTCHours())+":"+pad(date.getUTCMinutes())+":"+pad(date.getUTCSeconds())+"."+((date.getUTCMilliseconds()/1e3).toFixed(3)+"").slice(2,5)+"Z"}module.exports=toISOString;